'                   SUBSTR(:p99_plan,1,3) ,SUBSTR(:p99_plan,4,3) ,CodRamoPlan ,',
'                   CodCobert , PARAMPRECOBERT ,NULL ,',
'                   NULL,''S'')sumaasegmax,                   ',
'                   PR_PRE_COBERT.PRE_TASA(:p99_IdCotiza ,1 ,:p99_producto ,',
'                   SUBSTR(:p99_plan,1,3) ,SUBSTR(:p99_plan,4,3) ,CodRamoPlan ,',
'                   CodCobert , PARAMPRECOBERT ,NULL ,',
