'            CodProd   = :p99_Producto,',
'            CodPlan   = :p99_Plan,',
'            CodMoneda = :p99_CodMoneda,',
'            Direccion = :p99_Direccion,',
'            FecDesde  = :p99_Desde,',
'            FecHasta  = :p99_Hasta,',
'            CodInter  = :p99_Intermediario,',
'            Apellido  = :p99_Apellido',
'      where IdCotiza  = :p99_IdCotiza;',
'',
'    -- Los Cambios de Efrain',
'    delete cotizacion_cobertura where IdCotiza  = :p99_IdCotiza;',
'',
'      insert into cotizacion_cobertura',
'     select :p99_IdCotiza, codramoplan||codcobert, indcobertoblig,PR_PRE_COBERT.PRE_SUMA(:p99_IdCotiza ,1 ,:p99_producto ,',
