'if :p99_idcotiza is not null then',
'   :p99_existe_registro := ''S'';',
'   begin',
'     select TipoId, NumId, DvId, Nombre, Email, Telefono, NroMovil,',
'            StsCotiza, CodProd, CodPlan,',
'            CodMoneda, Direccion, FecDesde, FecHasta, CodInter,',
