'      where IdCotiza = :p99_idcotiza;',
'   exception',
'      when no_data_found then ',
'           :p99_TipoId   := null; :p99_NumId     := null; :p99_DvId          := null;',
'           :p99_Nombre   := null; :p99_Correo    := null; :p99_Telefono      := null;',
'           :p99_Celular  := null; :p99_Estatus   := null; :p99_Producto      := null;',
'           :p99_Plan     := null; :p99_CodMoneda := null; :p99_Direccion     := null; ',
