,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'begin',
' if :p199_existe_registro = ''N'' then ',
'    :p199_existe_registro := ''S'';',
'    insert into cotizacion',
'         ( IdCotiza, TipoId, NumId, DvId, Nombre, Email, Telefono, NroMovil,',
'           FechaReg, StsCotiza, DiasCotiza, CodProd, CodPlan,',
'           CodMoneda, Direccion, FecDesde, FecHasta, CodInter,',
'           Apellido, IdCotizaPlantilla,CodOfi)',
'     values',
'        ( :p199_IdCotiza, :p199_TipoId, :p199_NumId, :p199_DvId, :p199_Nombre, :p199_Correo, :p199_Telefono,',
'         :p199_Celular,sysdate, :p199_Estatus, 15, :p199_Producto, :p199_Plan, :p199_CodMoneda,',
'         :p199_Direccion,:p199_Desde, :p199_Hasta, :p199_Intermediario, :p199_Apellido, :p199_cotizaplantilla,',
'         :p199_codofi);',
'     -- Insertar Coberturas  ',
'     -- Incluyendo cambios de Efrain - PreCobert',
'     insert into cotizacion_cobertura',
'     select :p199_IdCotiza, codramoplan||codcobert, indcobertoblig,PR_PRE_COBERT.PRE_SUMA(:p199_IdCotiza ,1 ,:p199_producto ,',
'                   SUBSTR(:p199_plan,1,3) ,SUBSTR(:p199_plan,4,3) ,CodRamoPlan ,',
'                   CodCobert , PARAMPRECOBERT ,NULL ,',
'                   NULL,''S'')sumaasegmax,                   ',
'                   PR_PRE_COBERT.PRE_TASA(:p199_IdCotiza ,1 ,:p199_producto ,',
'                   SUBSTR(:p199_plan,1,3) ,SUBSTR(:p199_plan,4,3) ,CodRamoPlan ,',
'                   CodCobert , PARAMPRECOBERT ,NULL ,',
'                   NULL,NULL ,NULL ,NULL,''S'') tasamax,',
'                   ',
'                   PR_PRE_COBERT.PRE_SUMA(:p199_IdCotiza ,1 ,:p199_producto ,',
'                   SUBSTR(:p199_plan,1,3) ,SUBSTR(:p199_plan,4,3) ,CodRamoPlan ,',
'                   CodCobert , PARAMPRECOBERT ,NULL ,',
'                   NULL,''S'') * PR_PRE_COBERT.PRE_TASA(:p199_IdCotiza ,1 ,:p199_producto ,',
'                   SUBSTR(:p199_plan,1,3) ,SUBSTR(:p199_plan,4,3) ,CodRamoPlan ,',
'                   CodCobert , PARAMPRECOBERT ,NULL ,',
'                   NULL,NULL ,NULL ,NULL,''S'') /100 primamax,',
'                   null,codramoplan',
'       from cobert_plan_prod',
'       where codplan||revplan = :p199_plan',
'         and codprod = :p199_producto',
'         and indcobertoblig =''S'';',
'',
'     -- Insertar Clausulas',
'     insert into cotiza_clausula',
'        select :p199_IdCotiza,cl.codclau, cp.codramo, cp.indclauoblig, :p199_Desde, :p199_Hasta,null,null',
'          from clausula cl, clau_ramo_plan cp',
'         where cl.codclau = cp.codclau',
'           and cp.codplan||revplan = :p199_plan',
'           and cp.codprod = :p199_producto;   ',
'     -- Insertar Requisitos.',
'     insert into cotizacion_requisito',
'        select codprod, codplan||revplan, codramoplan, codreq, indoblig, sysdate, ',
'               seq_cotizacion_requisito.nextval,''PEN'',:p199_IdCotiza,null,null,null,null',
'          from req_plan_prod',
'         where codplan||revplan = :p199_plan',
'           and codprod          = :p199_producto;',
'     -- Insertar Anexos',
'     insert into cotiza_anexo',
'        select :p199_IdCotiza, an.codanexo, en.codramoplan,''N'',:p199_Desde, :p199_Hasta, null',
'          from anexo an, endoso_ramo_plan_prod en ',
'         where an.codanexo = en.codanexo',
'           and en.codplan||en.revplan = :p199_plan',
'           and en.codprod             = :p199_producto;',
' elsif :p199_existe_registro = ''S'' then',
'     update cotizacion',
'        set Tipoid    = :p199_TipoId,',
'            NumId     = :p199_NumId,',
'            DvId      = :p199_DvId,',
'            Nombre    = :p199_Nombre,',
'            Email     = :p199_Correo,',
'            Telefono  = :p199_Telefono,',
'            NroMovil  = :p199_Celular,',
'            StsCotiza = :p199_Estatus, ',
'            CodProd   = :p199_Producto,',
'            CodPlan   = :p199_Plan,',
'            CodMoneda = :p199_CodMoneda,',
'            Direccion = :p199_Direccion,',
'            FecDesde  = :p199_Desde,',
'            FecHasta  = :p199_Hasta,',
'            CodInter  = :p199_Intermediario,',
'            Apellido  = :p199_Apellido',
'      where IdCotiza  = :p199_IdCotiza;',
'',
'    -- Los Cambios de Efrain',
'    delete cotizacion_cobertura where IdCotiza  = :p199_IdCotiza;',
'',
'      insert into cotizacion_cobertura',
'     select :p199_IdCotiza, codramoplan||codcobert, indcobertoblig,PR_PRE_COBERT.PRE_SUMA(:p199_IdCotiza ,1 ,:p199_producto ,',
'                   SUBSTR(:p199_plan,1,3) ,SUBSTR(:p199_plan,4,3) ,CodRamoPlan ,',
'                   CodCobert , PARAMPRECOBERT ,NULL ,',
'                   NULL,''S'')sumaasegmax,   ',
'                                   ',
'                   PR_PRE_COBERT.PRE_TASA(:p199_IdCotiza ,1 ,:p199_producto ,',
'                   SUBSTR(:p199_plan,1,3) ,SUBSTR(:p199_plan,4,3) ,CodRamoPlan ,',
'                   CodCobert , PARAMPRECOBERT ,NULL ,',
'                   NULL,NULL ,NULL ,NULL,''S'') tasamax,',
'                   ',
'                   ',
'                   PR_PRE_COBERT.PRE_SUMA(:p199_IdCotiza ,1 ,:p199_producto ,',
'                   SUBSTR(:p199_plan,1,3) ,SUBSTR(:p199_plan,4,3) ,CodRamoPlan ,',
'                   CodCobert , PARAMPRECOBERT ,NULL ,',
'                   NULL,''S'') * PR_PRE_COBERT.PRE_TASA(:p199_IdCotiza ,1 ,:p199_producto ,',
'                   SUBSTR(:p199_plan,1,3) ,SUBSTR(:p199_plan,4,3) ,CodRamoPlan ,',
